function initCardAnimations(){Array.from(document.querySelectorAll(".anim")).forEach((function(t,e){setTimeout((function(){t.style.opacity="1",t.style.transform="translate3D(0, 0, 0) rotate3D(0, 0, 0, 0)"}),50*e)}))}initCardAnimations();let touchStartY=0,touchEndY=0;function toggleMenu(){const t=document.querySelector(".header"),e=document.querySelector(".nav-toggle"),o=document.querySelector(".main-container");t.classList.toggle("opened"),e.classList.toggle("cross"),o.classList.toggle("slided")}function toggleTopBar(){const t=document.querySelector(".header");window.scrollY>=600?t.classList.add("fixed"):t.classList.remove("fixed")}document.addEventListener("touchstart",(function(t){touchStartY=t.touches[0].clientY}),!1),document.addEventListener("touchmove",(function(t){touchEndY=t.touches[0].clientY}),!1),document.addEventListener("touchend",(function(t){window.scrollY<=0&&touchEndY>touchStartY+50&&toggleMenu()}),!1),document.addEventListener("click",(function(t){const e=document.querySelector(".header");e.contains(t.target)||e.classList.remove("opened")})),window.addEventListener("scroll",toggleTopBar);